<script>
export default {
  props: {
    accountInfo: Object,
  },
  data() {
    return {
      account: this.accountInfo,
    };
  },
  computed: {
    myPayment() {
      return Number(this.account.myPayment).toFixed(8);
    },
    todayProfit() {
      return Number(this.account.todayProfit).toFixed(8);
    },
    hashRate() {
      return Number(this.account.hashRate).toFixed(2);
    },
  },
};
